(()=>{"use strict";((e,t=!1)=>{const n=document.getElementById("timer"),m=document.getElementById("timer-days"),i=document.getElementById("timer-remaining"),a=new Date(e).getTime();let r;const o=["Воскресенье","Понедельник","Вторник","Среда","Четверг","Пятница","Суббота"];r=setInterval((()=>{const e=(()=>{const e=new Date,n=(a-e.getTime())/1e3,m=Math.floor(n/3600/24);let i=Math.floor(n/3600);i=t?i%24:i;return{timeRemaining:n,days:(r=m,r<10?"0"+r:""+r),timerMeridiem:e.toLocaleTimeString("en-US"),weekName:o[e.getDay()]};var r})();n.textContent=e.timerMeridiem,m.textContent=e.weekName,i.textContent=e.days}),1e3)})("01 january 2023")})();